create table sys_function
(
    functionId       int auto_increment comment '功能ID'
        primary key,
    functionParentId int                                null comment '功能的父ID',
    functionType     varchar(255)                       null comment '功能类型[menu/permission]',
    functionIsParent int      default 0                 null comment '0子节点 1父节点',
    functionCode     varchar(255)                       null comment '功能编码[只有type= permission才有  user:view]',
    functionName     varchar(255)                       null comment '功能名称',
    functionIcon     varchar(255)                       null comment '功能图标',
    functionHref     varchar(255)                       null comment '功能链接',
    functionTarget   varchar(255)                       null comment '功能链接',
    functionIsOpen   int                                null comment '功能是否展开',
    functionOrderNum int                                null comment '功能编码',
    state            int      default 1                 null comment '功能状态【0不可用1可用】',
    remark           varchar(255)                       null,
    createTime       datetime default CURRENT_TIMESTAMP null,
    createPerson     int                                null
)
    charset = utf8;

